import React, { useState } from 'react';
import axios from 'axios';
import './LoanForm.css'; // Adjust the path based on the location of your CSS file
import PersonalInformation from './PersonalInformation';
import FinancialInformation from './FinancialInformation';
import CreditInformation from './CreditInformation';
import EmploymentInformation from './EmploymentInformation';
import FinancialDetails from './FinancialDetails';
import PersonalDetails from './PersonalDetails';

const LoanForm = () => {
  const [formData, setFormData] = useState({
    Full_Name: '',
    Address: '',
    PAN_Number: '',
    Company_Name: '',
    Current_Salary: '',
    Previous_Salary: '',
    Owns_House: '',
    Rent_Amount: '',
    Grocery_Expense: '',
    Current_EMIs: '',
    Credit_Score: '',
    Number_of_Credit_Cards: '',
    Total_Credit_Limit: '',
    Number_of_Loans: '',
    Employment_Duration: '',
    Job_Title: '',
    Bonuses_Allowances: '',
    Savings: '',
    Investments: '',
    Monthly_Expenses: '',
    Age: '',
    Education_Level: '',
    Marital_Status: '',
    Dependents: '',
    Loan_Purpose: '',
    Mall_Visits_Per_Month: '',
    Mall_Spend_Average: ''
  });

  const [step, setStep] = useState(1);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const nextStep = () => {
    setStep(step + 1);
  };

  const prevStep = () => {
    setStep(step - 1);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://127.0.0.1:5000/predict', formData);
      console.log('Prediction Results:', response.data);
      // Handle the response as needed
    } catch (error) {
      console.error('Error making prediction:', error.response ? error.response.data : error.message);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      {step === 1 && <PersonalInformation formData={formData} handleChange={handleChange} nextStep={nextStep} />}
      {step === 2 && <FinancialInformation formData={formData} handleChange={handleChange} nextStep={nextStep} prevStep={prevStep} />}
      {step === 3 && <CreditInformation formData={formData} handleChange={handleChange} nextStep={nextStep} prevStep={prevStep} />}
      {step === 4 && <EmploymentInformation formData={formData} handleChange={handleChange} nextStep={nextStep} prevStep={prevStep} />}
      {step === 5 && <FinancialDetails formData={formData} handleChange={handleChange} nextStep={nextStep} prevStep={prevStep} />}
      {step === 6 && <PersonalDetails formData={formData} handleChange={handleChange} prevStep={prevStep} handleSubmit={handleSubmit} />}
    </form>
  );
};

