import React, { useState } from 'react';
import axios from 'axios';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { useNavigate } from 'react-router-dom';
import './LoanForm.css'; // Ensure this path is correct

const LoanForm = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    Full_Name: '',
    Address: '',
    PAN_Number: '',
    Company_Name: '',
    Current_Salary: '',
    Previous_Salary: '',
    Owns_House: '',
    Rent_Amount: '',
    Grocery_Expense: '',
    Current_EMIs: '',
    Date_Previous_Hike: '',
    Date_Next_Hike: '',
    Bank_Name: '',
    Credit_Score: '',
    Number_of_Credit_Cards: '',
    Total_Credit_Limit: '',
    Number_of_Loans: '',
    Employment_Duration: '',
    Job_Title: '',
    Bonuses_Allowances: '',
    Savings: '',
    Investments: '',
    Monthly_Expenses: '',
    Age: '',
    Education_Level: '',
    Marital_Status: '',
    Dependents: '',
    Loan_Purpose: '',
    Mall_Visits_Per_Month: '',
    Mall_Spend_Average: ''
  });

  const bankOptions = [
    'State Bank of India', 'HDFC Bank', 'ICICI Bank', 'Axis Bank',
    'Kotak Mahindra Bank', 'Bank of Baroda', 'Punjab National Bank',
    'Canara Bank', 'Union Bank of India', 'Indian Bank', 'IndusInd Bank',
    'IDFC FIRST Bank', 'Bandhan Bank', 'Yes Bank', 'South Indian Bank',
    'Ujjivan Small Finance Bank', 'RBL Bank', 'Federal Bank',
    'Lakshmi Vilas Bank', 'Jammu & Kashmir Bank', 'Others'
  ];

  const jobTitleOptions = [
    'Software Engineer', 'Data Analyst', 'Product Manager', 'Financial Analyst',
    'Marketing Manager', 'Sales Executive', 'HR Manager', 'Consultant',
    'Graphic Designer', 'Project Manager', 'Business Analyst',
    'Operations Manager', 'Customer Service Executive', 'Accountant',
    'Content Writer', 'Web Developer', 'UX/UI Designer', 'Network Engineer',
    'Digital Marketer', 'Administrative Assistant', 'Research Scientist',
    'Others'
  ];

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevFormData => {
      if (name === 'Owns_House' && value === 'Yes') {
        return { ...prevFormData, [name]: value, Rent_Amount: '' };
      }
      return { ...prevFormData, [name]: value };
    });
  };

  const validateForm = () => {
    for (const key in formData) {
      if (formData[key] === '') return false;
    }
    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) {
      toast.error('Please check fields again');
      return;
    }

    try {
      const response = await axios.post('http://127.0.0.1:5000/predict', formData);
      console.log('Prediction Results:', response.data);
      toast.success('Loan application submitted successfully!');
      navigate('/prediction-result', { state: { predictionData: response.data } });
    } catch (error) {
      console.error('Error making prediction:', error.response ? error.response.data : error.message);
      toast.error('An error occurred while submitting the application.');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="form-all">
      <ToastContainer />
      {/* Personal Information Section */}
      <div className="form-section">
        <h2>Personal Information</h2>
        {/* Add form fields */}
      </div>
      {/* Financial Information Section */}
      <div className="form-section">
        <h2>Financial Information</h2>
        {/* Add form fields */}
      </div>
      {/* Additional Information Section */}
      <div className="form-section">
        <h2>Additional Information</h2>
        {/* Add form fields */}
      </div>
      <div className="form-row">
        <div className="form-line">
          <button type="submit" className="form-button">Submit</button>
        </div>
      </div>
    </form>
  );
};

