import React, { useState } from 'react';
import axios from 'axios';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './LoanForm.css'; // Ensure this path is correct
import { useNavigate } from 'react-router-dom'; // Updated import

const LoanForm = () => {
  const [formData, setFormData] = useState({
    Full_Name: '',
    Address: '',
    PAN_Number: '',
    Company_Name: '',
    Current_Salary: '',
    Previous_Salary: '',
    Owns_House: '',
    Rent_Amount: '',
    Grocery_Expense: '',
    Current_EMIs: '',
    Date_Previous_Hike: '',
    Date_Next_Hike: '',
    Bank_Name: '',
    Credit_Score: '',
    Number_of_Credit_Cards: '',
    Total_Credit_Limit: '',
    Number_of_Loans: '',
    Employment_Duration: '',
    Job_Title: '',
    Bonuses_Allowances: '',
    Savings: '',
    Investments: '',
    Monthly_Expenses: '',
    Age: '',
    Education_Level: '',
    Marital_Status: '',
    Dependents: '',
    Loan_Purpose: '',
    Mall_Visits_Per_Month: '',
    Mall_Spend_Average: '',
    Loans: [] // To hold dynamic loan fields
  });

  const bankOptions = [
    'State Bank of India',
    'HDFC Bank',
    'ICICI Bank',
    'Axis Bank',
    'Kotak Mahindra Bank',
    'Bank of Baroda',
    'Punjab National Bank',
    'Canara Bank',
    'Union Bank of India',
    'Indian Bank',
    'IndusInd Bank',
    'IDFC FIRST Bank',
    'Bandhan Bank',
    'Yes Bank',
    'South Indian Bank',
    'Ujjivan Small Finance Bank',
    'RBL Bank',
    'Federal Bank',
    'Lakshmi Vilas Bank',
    'Jammu & Kashmir Bank',
    'Others'
  ];

  const jobTitleOptions = [
    'Software Engineer',
    'Data Analyst',
    'Product Manager',
    'Financial Analyst',
    'Marketing Manager',
    'Sales Executive',
    'HR Manager',
    'Consultant',
    'Graphic Designer',
    'Project Manager',
    'Business Analyst',
    'Operations Manager',
    'Customer Service Executive',
    'Accountant',
    'Content Writer',
    'Web Developer',
    'UX/UI Designer',
    'Network Engineer',
    'Digital Marketer',
    'Administrative Assistant',
    'Research Scientist',
    'Others'
  ];

  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevFormData => {
      // Clear Rent_Amount if Owns_House is updated to "Yes"
      if (name === 'Owns_House' && value === 'Yes') {
        return {
          ...prevFormData,
          [name]: value,
          Rent_Amount: '' // Clear Rent_Amount when Owns_House is "Yes"
        };
      }

      // Handle dynamic loan fields
      if (name.startsWith('Loan_')) {
        const [loanIndex, field] = name.split('_').slice(1);
        const loans = [...prevFormData.Loans];
        loans[loanIndex] = { ...loans[loanIndex], [field]: value };
        return {
          ...prevFormData,
          Loans: loans
        };
      }

      // Handle other fields
      return {
        ...prevFormData,
        [name]: value
      };
    });
  };

  const handleNumberOfLoansChange = (e) => {
    const numberOfLoans = parseInt(e.target.value, 10);
    const loans = Array(numberOfLoans).fill({ EMI: '', Tenure: '' });
    setFormData(prevFormData => ({
      ...prevFormData,
      Number_of_Loans: e.target.value,
      Loans: loans
    }));
  };

  const validateForm = () => {
    for (const key in formData) {
      if (formData[key].trim() === '' && key !== 'Loans') {
        return false;
      }
    }
    for (const loan of formData.Loans) {
      if (loan.EMI.trim() === '' || loan.Tenure.trim() === '') {
        return false;
      }
    }
    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) {
      toast.error('Please check fields again');
      return;
    }

    try {
      const response = await axios.post('http://127.0.0.1:5000/predict', formData);
      const data = response.data;
      console.log('Prediction Results:', response.data);
      toast.success('Loan application submitted successfully!');
      navigate('/result', { state: { resultData: data } });
    } catch (error) {
      console.error('Error making prediction:', error.response ? error.response.data : error.message);
      toast.error('An error occurred while submitting the application.');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="form-all">
      <ToastContainer />
      {/* Personal Information Section */}
      <div className="form-section">
        <h2>Personal Information</h2>
        {/* Other personal information fields */}
        {/* Example: */}
        <div className="form-row">
          <div className="form-line">
            <label className="form-label">
              Full Name
              <input
                type="text"
                name="Full_Name"
                value={formData.Full_Name}
                onChange={handleChange}
                className="form-textbox"
              />
            </label>
          </div>
          {/* Add other personal fields similarly */}
        </div>
      </div>

      {/* Financial Information Section */}
      <div className="form-section">
        <h2>Financial Information</h2>
        {/* Other financial information fields */}
        {/* Example: */}
        <div className="form-row">
          <div className="form-line">
            <label className="form-label">
              Number of Loans
              <input
                type="number"
                name="Number_of_Loans"
                value={formData.Number_of_Loans}
                onChange={handleNumberOfLoansChange}
                min="0"
                className="form-textbox"
              />
            </label>
          </div>
          {/* Add other financial fields similarly */}
        </div>
      </div>

      {/* Dynamic Loan Fields Section */}
      <div className="form-section">
        <h2>Loan Details</h2>
        {formData.Loans.map((loan, index) => (
          <div key={index} className="form-row">
            <div className="form-line">
              <label className="form-label">
                Loan {index + 1} EMI
                <input
                  type="number"
                  name={`Loan_${index}_EMI`}
                  value={loan.EMI}
                  onChange={handleChange}
                  min="0"
                  className="form-textbox"
                />
              </label>
            </div>
            <div className="form-line">
              <label className="form-label">
                Loan {index + 1} Tenure
                <input
                  type="number"
                  name={`Loan_${index}_Tenure`}
                  value={loan.Tenure}
                  onChange={handleChange}
                  min="0"
                  className="form-textbox"
                />
              </label>
            </div>
          </div>
        ))}
      </div>

      {/* Other Information Section */}
      <div className="form-section">
        <h2>Other Information</h2>
        {/* Other fields */}
        {/* Example: */}
        <div className="form-row">
          <div className="form-line">
            <label className="form-label">
              Mall Visits Per Month
              <input
                type="number"
                name="Mall_Visits_Per_Month"
                value={formData.Mall_Visits_Per_Month}
                onChange={handleChange}
                min="0"
                className="form-textbox"
              />
            </label>
          </div>
          {/* Add other fields similarly */}
        </div>
      </div>

      <button type="submit" className="form-submit-button">Submit</button>
    </form>
  );
};

export default LoanForm;
