import React, { useState } from 'react';
import axios from 'axios';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './LoanForm.css';
import { useNavigate } from 'react-router-dom';

const LoanForm = () => {
  const [formData, setFormData] = useState({
    Full_Name: '',
    Address: '',
    PAN_Number: '',
    Company_Name: '',
    Current_Salary: '',
    Previous_Salary: '',
    Owns_House: '',
    Rent_Amount: '',
    Grocery_Expense: '',
    Current_EMIs: '',
    Date_Previous_Hike: '',
    Date_Next_Hike: '',
    Bank_Name: '',
    Credit_Score: '',
    Number_of_Credit_Cards: '',
    Total_Credit_Limit: '',
    Number_of_Loans: '',
    Employment_Duration: '',
    Job_Title: '',
    Bonuses_Allowances: '',
    Savings: '',
    Investments: '',
    Monthly_Expenses: '',
    Age: '',
    Education_Level: '',
    Marital_Status: '',
    Dependents: '',
    Loan_Purpose: '',
    Mall_Visits_Per_Month: '',
    Mall_Spend_Average: '',
    Loans: [],
  });

  const bankOptions = [
    'State Bank of India', 'HDFC Bank', 'ICICI Bank', 'Axis Bank', 'Kotak Mahindra Bank', 
    'Bank of Baroda', 'Punjab National Bank', 'Canara Bank', 'Union Bank of India', 
    'Indian Bank', 'IndusInd Bank', 'IDFC FIRST Bank', 'Bandhan Bank', 'Yes Bank', 
    'South Indian Bank', 'Ujjivan Small Finance Bank', 'RBL Bank', 'Federal Bank', 
    'Lakshmi Vilas Bank', 'Jammu & Kashmir Bank', 'Others'
  ];

  const jobTitleOptions = [
    'Software Engineer', 'Data Analyst', 'Product Manager', 'Financial Analyst', 
    'Marketing Manager', 'Sales Executive', 'HR Manager', 'Consultant', 'Graphic Designer', 
    'Project Manager', 'Business Analyst', 'Operations Manager', 'Customer Service Executive', 
    'Accountant', 'Content Writer', 'Web Developer', 'UX/UI Designer', 'Network Engineer', 
    'Digital Marketer', 'Administrative Assistant', 'Research Scientist', 'Others'
  ];
  
  const navigate = useNavigate();
  
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevFormData => {
      if (name === 'Owns_House' && value === 'Yes') {
        return {
          ...prevFormData,
          [name]: value,
          Rent_Amount: '' // Clear Rent_Amount when Owns_House is "Yes"
        };
      }
      return {
        ...prevFormData,
        [name]: value
      };
    });
  };

  const validateForm = () => {
    const requiredFields = [
      'Full_Name', 'Address', 'PAN_Number', 'Company_Name', 'Current_Salary', 'Employment_Duration',
      'Job_Title', 'Credit_Score', 'Bank_Name', 'Education_Level', 'Marital_Status', 'Dependents', 
      'Loan_Purpose', 'Age', 'Savings', 'Investments', 'Monthly_Expenses'
    ];

    for (const field of requiredFields) {
      if (formData[field].trim() === '') {
        return false; // Return false if any required field is empty
      }
    }
    
    // Ensure the Number_of_Loans field is a positive integer if set
    const numberOfLoans = parseInt(formData.Number_of_Loans, 10);
    if (isNaN(numberOfLoans) || numberOfLoans < 0) {
      return false;
    }

    // Check if all loan details are filled
    if (numberOfLoans > 0) {
      for (const loan of formData.Loans) {
        if (loan.EMI.trim() === '' || loan.Tenure.trim() === '') {
          return false;
        }
      }
    }

    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) {
      toast.error('Please fill out all required fields correctly.');
      return;
    }

    try {
      const response = await axios.post('http://127.0.0.1:5000/predict', formData);
      const data = response.data;
      toast.success('Loan application submitted successfully!');
      navigate('/result', { state: { resultData: data } });
    } catch (error) {
      console.error('Error making prediction:', error.response ? error.response.data : error.message);
      toast.error('An error occurred while submitting the application.');
    }
  };

  const handleNumberOfLoansChange = (e) => {
    const numberOfLoans = parseInt(e.target.value, 10);
    const loans = Array(numberOfLoans).fill({ EMI: '', Tenure: '' });
    setFormData(prevFormData => ({
      ...prevFormData,
      Number_of_Loans: e.target.value,
      Loans: loans
    }));
  };

  const handleChangeform = (e) => {
    const { name, value } = e.target;
    
    if (name.startsWith('Loan_')) {
      const [loanIndex, field] = name.split('_').slice(1);
      const loans = [...formData.Loans];
      if (loanIndex < loans.length) {
        loans[loanIndex] = { ...loans[loanIndex], [field]: value };
        setFormData(prevFormData => ({
          ...prevFormData,
          Loans: loans
        }));
      }
    } else {
      handleChange(e);
    }
  };
  
  return (
    <form onSubmit={handleSubmit} className="form-all">
      <ToastContainer />
      {/* Personal Information Section */}
      <div className="form-section">
        <h2>Personal Information</h2>
        <div className="form-row">
          <div className="form-line">
            <label className="form-label">
              Full Name
              <input
                type="text"
                name="Full_Name"
                value={formData.Full_Name}
                onChange={handleChange}
                className="form-textbox"
                required
              />
            </label>
          </div>
          <div className="form-line">
            <label className="form-label">
              Address
              <input
                type="text"
                name="Address"
                value={formData.Address}
                onChange={handleChange}
                className="form-textbox"
                required
              />
            </label>
          </div>
        </div>
        <div className="form-row">
          <div className="form-line">
            <label className="form-label">
              PAN Number
              <input
                type="text"
                name="PAN_Number"
                value={formData.PAN_Number}
                onChange={handleChange}
                className="form-textbox"
                required
              />
            </label>
          </div>
          <div className="form-line">
            <label className="form-label">
              Age
              <input
                type="number"
                name="Age"
                value={formData.Age}
                onChange={handleChange}
                min="0"
                className="form-textbox"
                required
              />
            </label>
          </div>
        </div>
        <div className="form-row">
          <div className="form-line">
            <label className="form-label">
              Education Level
              <select
                name="Education_Level"
                value={formData.Education_Level}
                onChange={handleChange}
                className="form-dropdown"
                required
              >
                <option value="">Select</option>
                <option value="Graduate">Graduate</option>
                <option value="PostGraduate">PostGraduate</option>
                <option value="PhD">PhD</option>
              </select>
            </label>
          </div>
          <div className="form-line">
            <label className="form-label">
              Marital Status
              <select
                name="Marital_Status"
                value={formData.Marital_Status}
                onChange={handleChange}
                className="form-dropdown"
                required
              >
                <option value="">Select</option>
                <option value="Single">Single</option>
                <option value="Married">Married</option>
                <option value="Widowed">Widowed</option>
                <option value="Divorced">Divorced</option>
              </select>
            </label>
          </div>
        </div>
        <div className="form-row">
          <div className="form-line">
            <label className="form-label">
              Dependents
              <input
                type="number"
                name="Dependents"
                value={formData.Dependents}
                onChange={handleChange}
                min="0"
                className="form-textbox"
                required
              />
            </label>
          </div>
          <div className="form-line">
            <label className="form-label">
              Savings
              <input
                type="number"
                name="Savings"
                value={formData.Savings}
                onChange={handleChange}
                min="0"
                className="form-textbox"
                required
              />
            </label>
          </div>
        </div>
        <div className="form-row">
          <div className="form-line">
            <label className="form-label">
              Investments
              <input
                type="number"
                name="Investments"
                value={formData.Investments}
                onChange={handleChange}
                min="0"
                className="form-textbox"
                required
              />
            </label>
          </div>
          <div className="form-line">
            <label className="form-label">
              Monthly Expenses
              <input
                type="number"
                name="Monthly_Expenses"
                value={formData.Monthly_Expenses}
                onChange={handleChange}
                min="0"
                className="form-textbox"
                required
              />
            </label>
          </div>
        </div>
      </div>
      
      {/* Employment Details Section */}
      <div className="form-section">
        <h2>Employment Details</h2>
        <div className="form-row">
          <div className="form-line">
            <label className="form-label">
              Company Name
              <input
                type="text"
                name="Company_Name"
                value={formData.Company_Name}
                onChange={handleChange}
                className="form-textbox"
                required
              />
            </label>
          </div>
          <div className="form-line">
            <label className="form-label">
              Job Title
              <select
                name="Job_Title"
                value={formData.Job_Title}
                onChange={handleChange}
                className="form-dropdown"
                required
              >
                <option value="">Select</option>
                {jobTitleOptions.map((title, index) => (
                  <option key={index} value={title}>{title}</option>
                ))}
              </select>
            </label>
          </div>
        </div>
        <div className="form-row">
          <div className="form-line">
            <label className="form-label">
              Current Salary
              <input
                type="number"
                name="Current_Salary"
                value={formData.Current_Salary}
                onChange={handleChange}
                min="0"
                className="form-textbox"
                required
              />
            </label>
          </div>
          <div className="form-line">
            <label className="form-label">
              Previous Salary
              <input
                type="number"
                name="Previous_Salary"
                value={formData.Previous_Salary}
                onChange={handleChange}
                min="0"
                className="form-textbox"
                required
              />
            </label>
          </div>
        </div>
        <div className="form-row">
          <div className="form-line">
            <label className="form-label">
              Employment Duration
              <input
                type="text"
                name="Employment_Duration"
                value={formData.Employment_Duration}
                onChange={handleChange}
                className="form-textbox"
                required
              />
            </label>
          </div>
          <div className="form-line">
            <label className="form-label">
              Bonuses and Allowances
              <input
                type="number"
                name="Bonuses_Allowances"
                value={formData.Bonuses_Allowances}
                onChange={handleChange}
                min="0"
                className="form-textbox"
                required
              />
            </label>
          </div>
        </div>
      </div>
      
      {/* Financial Information Section */}
      <div className="form-section">
        <h2>Financial Information</h2>
        <div className="form-row">
          <div className="form-line">
            <label className="form-label">
              Credit Score
              <input
                type="text"
                name="Credit_Score"
                value={formData.Credit_Score}
                onChange={handleChange}
                className="form-textbox"
                required
              />
            </label>
          </div>
          <div className="form-line">
            <label className="form-label">
              Bank Name
              <select
                name="Bank_Name"
                value={formData.Bank_Name}
                onChange={handleChange}
                className="form-dropdown"
                required
              >
                <option value="">Select</option>
                {bankOptions.map((bank, index) => (
                  <option key={index} value={bank}>{bank}</option>
                ))}
              </select>
            </label>
          </div>
        </div>
        <div className="form-row">
          <div className="form-line">
            <label className="form-label">
              Number of Credit Cards
              <input
                type="number"
                name="Number_of_Credit_Cards"
                value={formData.Number_of_Credit_Cards}
                onChange={handleChange}
                min="0"
                className="form-textbox"
                required
              />
            </label>
          </div>
          <div className="form-line">
            <label className="form-label">
              Total Credit Limit
              <input
                type="number"
                name="Total_Credit_Limit"
                value={formData.Total_Credit_Limit}
                onChange={handleChange}
                min="0"
                className="form-textbox"
                required
              />
            </label>
          </div>
        </div>
      </div>
      
      {/* Loan Information Section */}
      <div className="form-section">
        <h2>Loan Information</h2>
        <div className="form-row">
          <div className="form-line">
            <label className="form-label">
              Number of Loans
              <input
                type="number"
                name="Number_of_Loans"
                value={formData.Number_of_Loans}
                onChange={handleNumberOfLoansChange}
                min="0"
                className="form-textbox"
                required
              />
            </label>
          </div>
        </div>
        {formData.Number_of_Loans > 0 && (
          <>
            {formData.Loans.map((loan, index) => (
              <div key={index} className="loan-details">
                <h3>Loan {index + 1}</h3>
                <div className="form-row">
                  <div className="form-line">
                    <label className="form-label">
                      EMI
                      <input
                        type="text"
                        name={`Loan_${index}_EMI`}
                        value={loan.EMI}
                        onChange={handleChangeform}
                        className="form-textbox"
                        required
                      />
                    </label>
                  </div>
                  <div className="form-line">
                    <label className="form-label">
                      Tenure
                      <input
                        type="text"
                        name={`Loan_${index}_Tenure`}
                        value={loan.Tenure}
                        onChange={handleChangeform}
                        className="form-textbox"
                        required
                      />
                    </label>
                  </div>
                </div>
              </div>
            ))}
          </>
        )}
      </div>
      
      <div className="form-buttons">
        <button type="submit" className="form-submit">Submit</button>
      </div>
    </form>
  );
};

export default LoanForm;
