import React, { useState } from 'react';
import axios from 'axios';

function LoanForm() {
    const [formData, setFormData] = useState({
        // Initialize form data with default values or empty strings
        Current_Salary: '',
        Previous_Salary: '',
        Owns_House: '',
        Rent_Amount: '',
        Grocery_Expense: '',
        Current_EMIs: '',
        Date_Previous_Hike: '',
        Date_Next_Hike: '',
        Credit_Score: '',
        Number_of_Credit_Cards: '',
        Total_Credit_Limit: '',
        Number_of_Loans: '',
        Employment_Duration: '',
        Bonuses_Allowances: '',
        Savings: '',
        Investments: '',
        Monthly_Expenses: '',
        Age: '',
        Marital_Status: '',
        Dependents: '', // Ensure this matches the expected values (e.g., '0', '1', '2', etc.)
        Loan_Purpose: '',
        Mall_Visits_Per_Month: '',
        Mall_Spend_Average: ''
    });

    const [result, setResult] = useState(null);
    const [error, setError] = useState(null);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://127.0.0.1:5000/predict', formData);
            setResult(response.data);
            setError(null);  // Clear any previous errors
        } catch (error) {
            console.error('Error making prediction:', error.response ? error.response.data : error.message);
            setError('Error making prediction: ' + (error.response ? error.response.data : error.message));
        }
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                {/* Render form fields */}
                {Object.keys(formData).map((key) => (
                    <div key={key}>
                        <label htmlFor={key}>{key.replace(/_/g, ' ')}:</label>
                        <input
                            type="text"
                            name={key}
                            value={formData[key]}
                            onChange={handleChange}
                            placeholder={key.replace(/_/g, ' ')}
                        />
                    </div>
                ))}
                <button type="submit">Submit</button>
            </form>

            {result && (
                <div>
                    <h3>Loan Details:</h3>
                    <p><strong>Eligibility:</strong> {result.Eligibility}</p>
                    <p><strong>Requested Loan Amount:</strong> {result.Requested_Loan_Amount}</p>
                    <p><strong>Preferred Repayment Period:</strong> {result.Preferred_Repayment_Period}</p>
                    <p><strong>EMI:</strong> {result.EMI}</p>
                    <p><strong>Risk Score:</strong> {result.Risk_Score}</p>
                </div>
            )}

            {error && <p>{error}</p>}
        </div>
    );
}

export default LoanForm;
